// Generated by Selenium IDE
const { Builder, By, Key, until } = require('selenium-webdriver')
const assert = require('assert')

describe('Inscription mot de passe incorrect', function() {
  this.timeout(30000)
  let driver
  let vars
  beforeEach(async function() {
    driver = await new Builder().forBrowser('firefox').build()
    vars = {}
  })
  it('Inscription mot de passe incorrect', async function() {
    await driver.get("http://192.168.74.217/")
    await driver.findElement(By.id("connect_button_code")).click()
    await driver.findElement(By.id("inscription_button_code")).click()
    await driver.findElement(By.id("inputID")).click()
    await driver.findElement(By.id("inputID")).sendKeys("test@test.fr")
    await driver.findElement(By.id("inputMdp1")).click()
    await driver.findElement(By.id("inputMdp1")).sendKeys("azer")
    await driver.findElement(By.id("inputMdp2")).click()
    await driver.findElement(By.id("inputMdp2")).sendKeys("123")
    await driver.findElement(By.css(".btn-primary")).click()
    assert(await driver.findElement(By.id("retourMdp2")).getText() == "Les mots de passe ne sont pas identiques.")
    await driver.close()
  })
})
